import React, { useState } from 'react';
import { Select, Container, Paper, Typography, MenuItem, FormControlLabel, Checkbox} from '@material-ui/core';

import useStyle from './styles';

const Sidebar = () => {
    const classes = useStyle();
    const [age, setAge] = React.useState<string | number>('');
    const [open, setOpen] = React.useState(false);
    const [checked, setChecked] = React.useState(true)

    const toggleCheckbox = (event: React.ChangeEvent<HTMLInputElement>) => {
        setChecked(event.target.checked);
      };


    const handleChange = (event: React.ChangeEvent<{ value: unknown }>) => {
        setAge(event.target.value as number);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleOpen = () => {
        setOpen(true);
    };


    return (
        <Container className={classes.container}>
            <Paper elevation={10} className={classes.paper} >
                <Typography gutterBottom variant='subtitle2'>SELECT YEAR</Typography>
                <Select className={classes.margin} open={open} onClose={handleClose} onOpen={handleOpen} value={age} onChange={handleChange}>
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value={10}>Ten</MenuItem>
                    <MenuItem value={20}>Twenty</MenuItem>
                    <MenuItem value={30}>Thirty</MenuItem>
                </Select>
            </Paper>
            <div className={classes.severity} />
            <Paper elevation={10} className={classes.paper}>
                <Typography gutterBottom variant='subtitle2'>SELECT SEVERITY</Typography>
                <FormControlLabel control={ <Checkbox color="secondary" checked={checked} onChange={toggleCheckbox} inputProps={{"aria-label": "click unsubcribe"}} /> } label="Minor"/>
                <FormControlLabel control={ <Checkbox color="secondary" checked={checked} onChange={toggleCheckbox} inputProps={{"aria-label": "click unsubcribe"}} /> } label="Major"/>
                <FormControlLabel control={ <Checkbox color="secondary" checked={checked} onChange={toggleCheckbox} inputProps={{"aria-label": "click unsubcribe"}} /> } label="Critical"/>


            </Paper>
        </Container>
    )
}

export default Sidebar;
