import React, { useEffect, useState } from "react";
import { Paper, TableContainer, Table, TableHead, TableRow, TableCell, TableBody, Box } from "@material-ui/core";
import { Chart, AreaSeries, LineSeries } from '@devexpress/dx-react-chart-material-ui';
import useStyle from "./styles";

interface Column {
  id: 'cve' | 'date' | 'severity' | 'desc',
  label: string,
  minWidth?: number,
  align?: 'left',

};
const columns: Column[] = [
  { id: 'cve', label: 'CVE ID', minWidth: 170 },
  { id: 'date', label: 'PUBLISHED DATE', minWidth: 170 },
  { id: 'severity', label: 'SEVERITY', minWidth: 170 },
  { id: 'desc', label: 'DESCRIPTION', minWidth: 170 },
]
const generateDataa = (start, end, step) => {
  const dataa = [];
  for (let i = start; i < end; i += step) {
    dataa.push({ splineValue: Math.sin(i) / i, lineValue: ((i / 15) ** 2.718) - 0.2, argument: i });
  }

  return dataa;
};
const Dashboard = () => {
  const classes = useStyle();
  const [data, setData] = useState<any[]>([]);

  //const item = { cve: { CVE_data_meta: { ID: "number" }, description: { description_data: { value: "string" }}},  severity: "string" , publishedDate: "number"} 

  useEffect((start, end, step) => {
    fetch('http://localhost:5000/data')
      .then(response => response.json())
      .then(resData => setData(resData.data))
      for (let i = start; i < end; i += step) {
        dataa.push({ splineValue: Math.sin(i) / i, lineValue: ((i / 15) ** 2.718) - 0.2, argument: i });
      }

  }, []);


  // console.log(data)
  return (
    <Box className={classes.box}>
      <Paper className={classes.root}>
        <TableContainer className={classes.container}>
          <Table stickyHeader aria-label="sticky table">
            <TableHead>
              <TableRow>
                {columns.map((column) => (
                  <TableCell key={column.id} align={column.align} style={{ minWidth: column.minWidth }} >
                    {column.label}
                  </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {data.map(item =>
                <TableRow>
                  <TableCell key={item.id} align={item.align} style={{ minWidth: item.minWidth }}> {item.cve.CVE_data_meta.ID} </TableCell>
                  <TableCell key={item.id} align={item.align} style={{ minWidth: item.minWidth }} > {item.publishedDate} </TableCell>
                  <TableCell key={item.id} align={item.align} style={{ minWidth: item.minWidth }} > {item.impact.severity} </TableCell>
                  <TableCell key={item.id} align={item.align} style={{ minWidth: item.minWidth }} > {item.cve.description.description_data.value} </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
      <Paper className={classes.chart}>
      <Chart >
        {data.map(item => <LineSeries key={item.id} >{item.publishedDate}</LineSeries>)}
      </Chart>   

      </Paper>
    </Box>

  );
};

export default Dashboard;
